<script setup>
import { reactiveOmit } from "@vueuse/core";
import { cn } from "../../../utils";
import Progress from "../../elements/Progress.vue";
import TableRow from "./TableRow.vue";
const props = defineProps({
  size: { type: null, required: false, default: "2.5px" },
  enabled: { type: Boolean, required: false },
  class: { type: null, required: false },
  colspan: { type: Number, required: false },
  _tableCell: { type: Object, required: false },
  _tableRow: { type: Object, required: false },
  _tableProgress: { type: Object, required: false },
  una: { type: Object, required: false }
});
const delegatedProps = reactiveOmit(props, ["class"]);
</script>

<template>
  <TableRow
    :class="cn(
  'table-loading-row',
  props.una?.tableLoadingRow
)"
    data-loading="true"
    v-bind="delegatedProps._tableRow"
  >
    <td
      :class="
  cn(
    'table-loading-cell',
    props.una?.tableLoadingCell
  )
"
      :colspan="0"
      v-bind="delegatedProps._tableCell"
    >
      <div
        v-if="enabled"
        :class="cn(
  'table-loading',
  props.una?.tableLoading
)"
      >
        <slot>
          <Progress
            :size
            v-bind="props._tableProgress"
            :class="cn(
  props._tableProgress?.class
)"
          />
        </slot>
      </div>
    </td>
  </TableRow>
</template>
