<script setup>
import { computed } from "vue";
const props = defineProps({
  kbd: { type: String, required: false },
  label: { type: String, required: false },
  una: { type: Object, required: false }
});
const kbdVariants = ["solid", "outline"];
const hasVariant = computed(() => kbdVariants.some((kbdVariants2) => props.kbd?.includes(kbdVariants2)));
const isBaseVariant = computed(() => props.kbd?.includes("~"));
</script>

<template>
  <kbd
    class="kbd"
    :kbd="kbd"
    :class="[
  { 'kbd-default-variant': !hasVariant && !isBaseVariant },
  una?.kbd
]"
  >
    <slot>
      {{ label }}
    </slot>
  </kbd>
</template>
