<script setup>
import { reactiveOmit } from "@vueuse/core";
import { DropdownMenuItem, useForwardProps } from "reka-ui";
import { cn } from "../../../utils";
import Button from "../Button.vue";
import DropdownMenuShortcut from "./DropdownMenuShortcut.vue";
defineOptions({
  inheritAttrs: false
});
const props = defineProps({
  dropdownMenuItem: { type: null, required: false, default: "~" },
  inset: { type: Boolean, required: false },
  shortcut: { type: String, required: false },
  _dropdownMenuShortcut: { type: Object, required: false },
  una: { type: Object, required: false },
  onSelect: { type: Function, required: false },
  disabled: { type: Boolean, required: false },
  textValue: { type: String, required: false },
  asChild: { type: Boolean, required: false },
  as: { type: null, required: false },
  type: { type: String, required: false },
  loadingPlacement: { type: String, required: false },
  icon: { type: Boolean, required: false },
  reverse: { type: Boolean, required: false },
  loading: { type: Boolean, required: false },
  block: { type: Boolean, required: false },
  to: { type: null, required: false },
  label: { type: String, required: false },
  btn: { type: String, required: false },
  leading: { type: String, required: false },
  trailing: { type: String, required: false },
  size: { type: String, required: false, default: "sm" },
  square: { type: null, required: false },
  rounded: { type: null, required: false, default: "sm" },
  class: { type: null, required: false },
  breadcrumbActive: { type: String, required: false },
  breadcrumbInactive: { type: String, required: false },
  paginationSelected: { type: String, required: false },
  paginationUnselected: { type: String, required: false },
  dropdownMenu: { type: String, required: false },
  toggleOn: { type: String, required: false },
  toggleOff: { type: String, required: false },
  tabsActive: { type: String, required: false },
  tabsInactive: { type: String, required: false },
  navigationMenu: { type: String, required: false },
  navigationMenuLink: { type: String, required: false },
  ariaLabel: { type: String, required: false }
});
const slots = defineSlots();
const delegatedProps = reactiveOmit(props, ["class"]);
const forwardedProps = useForwardProps(delegatedProps);
</script>

<template>
  <div>
    <DropdownMenuItem
      as-child
    >
      <Button
        v-bind="{ ...forwardedProps, ...$attrs }"
        :dropdown-menu-item
        :class="cn(
  'dropdown-menu-item-base w-full justify-start font-normal px-0.5714285714285714em py-0.42857142857142855em h-auto',
  forwardedProps.inset && !(forwardedProps.leading || slots.leading) && 'pl-8',
  props.class
)"
        :rounded
        btn="~"
        :una="{
  btnLeading: cn('dropdown-menu-item-leading ml-0', forwardedProps.una?.btnLeading),
  btnTrailing: cn('dropdown-menu-item-trailing', forwardedProps.una?.btnTrailing),
  ...forwardedProps.una
}"
        @click="forwardedProps.onSelect"
      >
        <template v-for="(_, name) in $slots" #[name]="slotData">
          <slot :name="name" v-bind="slotData" />
        </template>

        <template
          v-if="forwardedProps.shortcut"
          #trailing
        >
          <DropdownMenuShortcut
            v-bind="_dropdownMenuShortcut"
          >
            {{ forwardedProps.shortcut }}
          </DropdownMenuShortcut>
        </template>
      </Button>
    </DropdownMenuItem>
  </div>
</template>
