<script setup>
import { reactivePick } from "@vueuse/core";
import { useForwardPropsEmits } from "reka-ui";
import TooltipContent from "./TooltipContent.vue";
import TooltipProvider from "./TooltipProvider.vue";
import TooltipRoot from "./TooltipRoot.vue";
import TooltipTrigger from "./TooltipTrigger.vue";
defineOptions({
  inheritAttrs: false
});
const props = defineProps({
  content: { type: String, required: false },
  tooltip: { type: null, required: false },
  _tooltipProvider: { type: Object, required: false },
  _tooltipTrigger: { type: Object, required: false },
  _tooltipContent: { type: Object, required: false },
  una: { type: Object, required: false },
  class: { type: null, required: false },
  size: { type: null, required: false },
  defaultOpen: { type: Boolean, required: false },
  open: { type: Boolean, required: false },
  delayDuration: { type: Number, required: false },
  disableHoverableContent: { type: Boolean, required: false },
  disableClosingTrigger: { type: Boolean, required: false },
  disabled: { type: Boolean, required: false },
  ignoreNonKeyboardFocus: { type: Boolean, required: false }
});
const emits = defineEmits(["update:open"]);
const rootProps = reactivePick(props, [
  "defaultOpen",
  "delayDuration",
  "disableClosingTrigger",
  "disabled",
  "disableHoverableContent",
  "ignoreNonKeyboardFocus",
  "open"
]);
const forwarded = useForwardPropsEmits(rootProps, emits);
</script>

<template>
  <TooltipProvider
    v-bind="_tooltipProvider"
  >
    <TooltipRoot
      v-bind="forwarded"
    >
      <TooltipTrigger
        as-child
        v-bind="_tooltipTrigger"
      >
        <slot />
      </TooltipTrigger>

      <TooltipContent
        v-if="$slots.content || content"
        v-bind="_tooltipContent"
        :size
        :tooltip
        :disabled
        :una
      >
        <slot name="content">
          {{ content }}
        </slot>
      </TooltipContent>
    </TooltipRoot>
  </TooltipProvider>
</template>
