<script setup>
import { reactiveOmit } from "@vueuse/core";
import {
  SelectItem,
  useForwardProps
} from "reka-ui";
import { cn } from "../../../utils";
import SelectItemIndicator from "./SelectItemIndicator.vue";
import SelectItemText from "./SelectItemText.vue";
const props = defineProps({
  selectItem: { type: null, required: false, default: "gray" },
  _selectItemText: { type: Object, required: false },
  _selectItemIndicator: { type: Object, required: false },
  una: { type: Object, required: false },
  value: { type: null, required: true },
  disabled: { type: Boolean, required: false },
  textValue: { type: String, required: false },
  asChild: { type: Boolean, required: false },
  as: { type: null, required: false },
  class: { type: null, required: false },
  size: { type: null, required: false }
});
const delegatedProps = reactiveOmit(props, ["class"]);
const forwardedProps = useForwardProps(delegatedProps);
</script>

<template>
  <SelectItem
    v-bind="forwardedProps"
    :class="
  cn(
    'select-item',
    props.una?.selectItem,
    props.class
  )
"
    :select-item
  >
    <SelectItemIndicator
      :una
      v-bind="props._selectItemIndicator"
    >
      <slot name="indicator" />
    </SelectItemIndicator>

    <SelectItemText
      v-bind="props._selectItemText"
    >
      <slot />
    </SelectItemText>
  </SelectItem>
</template>
