<script setup>
import { reactiveOmit } from "@vueuse/core";
import {
  ScrollAreaCorner,
  ScrollAreaRoot,
  ScrollAreaViewport
} from "reka-ui";
import { cn } from "../../utils";
import ScrollBar from "./ScrollBar.vue";
const props = defineProps({
  scrollArea: { type: null, required: false, default: "gray" },
  _scrollAreaRoot: { type: Object, required: false },
  _scrollAreaScrollbar: { type: Object, required: false },
  _scrollAreaViewport: { type: Object, required: false },
  una: { type: Object, required: false },
  type: { type: String, required: false },
  dir: { type: String, required: false },
  scrollHideDelay: { type: Number, required: false },
  asChild: { type: Boolean, required: false },
  as: { type: null, required: false },
  orientation: { type: String, required: false },
  class: { type: null, required: false },
  size: { type: null, required: false, default: "md" },
  rounded: { type: null, required: false, default: "full" }
});
const delegatedProps = reactiveOmit(props, ["class", "size", "rounded"]);
</script>

<template>
  <ScrollAreaRoot
    v-bind="delegatedProps"
    :class="cn(
  'scroll-area-root',
  props.una?.scrollAreaRoot,
  props.class
)"
  >
    <ScrollAreaViewport
      v-bind="_scrollAreaViewport"
      :class="cn(
  'scroll-area-viewport',
  props.una?.scrollAreaViewport,
  _scrollAreaViewport?.class
)"
    >
      <slot />
    </ScrollAreaViewport>
    <ScrollBar
      v-bind="_scrollAreaScrollbar"
      :orientation="orientation ?? _scrollAreaScrollbar?.orientation"
      :una
      :size
      :rounded
      :scroll-area
    />
    <ScrollAreaCorner />
  </ScrollAreaRoot>
</template>
